  1847→/**
  1848→ * Hacer scroll suave hacia una etapa específica después del movimiento.
  1849→ *
  1850→ * @param {jQuery} etapa_destino Etapa a la que hacer scroll.
  1851→ * @returns {void}
  1852→ * @author Andres Morales
  1853→ */
  1854→function scrollHaciaEtapa(etapa_destino) {
  1855→    var $contenedor = $('#contenido-etapas-lista');
  1856→
  1857→    if (!$contenedor.length || !etapa_destino.length) {
  1858→        return;
  1859→    }
  1860→
  1861→    var etapa_id = etapa_destino.data('etapa-id');
  1862→    var $etapa_actualizada = $(`.etapa-card[data-etapa-id="${etapa_id}"]`);
  1863→
  1864→    if (!$etapa_actualizada.length) {
  1865→        return;
  1866→    }
  1867→
  1868→    var desplazamiento_izquierda_contenedor = $contenedor.scrollLeft();
  1869→    var contenedor_ancho = $contenedor.outerWidth();
  1870→    var ancho_desplazamiento_contenedor = $contenedor[0].scrollWidth;
  1871→
  1872→    var posicion_etapa = $etapa_actualizada.position().left;
  1873→    var etapa_ancho = $etapa_actualizada.outerWidth();
  1874→
  1875→    var limite_izquierdo = desplazamiento_izquierda_contenedor;
  1876→    var limite_derecho = desplazamiento_izquierda_contenedor + contenedor_ancho;
  1877→
  1878→    var etapa_visible = (posicion_etapa >= limite_izquierdo) &&
  1879→                       ((posicion_etapa + etapa_ancho) <= limite_derecho);
  1880→
  1881→    if (etapa_visible) {
  1882→        return;
  1883→    }
  1884→
  1885→    var nueva_posicion_scroll = posicion_etapa - (contenedor_ancho / 2) + (etapa_ancho / 2);
  1886→
  1887→    nueva_posicion_scroll = Math.max(0, Math.min(nueva_posicion_scroll, ancho_desplazamiento_contenedor - contenedor_ancho));
  1888→
  1889→    $contenedor.animate(
  1890→        { scrollLeft: nueva_posicion_scroll },
  1891→        {
  1892→            duration: 600,
  1893→            easing: 'swing'
  1894→        }
  1895→    );
  1896→}
