   160→    private function guardarAspectoPuntaje(int $etapa_vacante_usuario_id, string $categoria, string $aspecto): void {
   161→        $this->aspecto_puntaje_repositorio->crearOActualizar([
   162→            'etapa_vacante_usuario_id' => $etapa_vacante_usuario_id,
   163→            'categoria'                => $categoria,
   164→            'aspecto'                  => $aspecto
   165→        ], [
   166→            'puntaje'                  => $this->puntaje,
   167→            'cumple'                   => $this->cumple
   168→        ]);
   169→
   170→        $this->puntaje_total += $this->puntaje;
   171→        $this->establecerPuntajeCumplimiento();
