   174→    /**
   175→     * Generar adecuación de hoja de vida.
   176→     *
   177→     * @param int $id ID de la etapa del usuario en la vacante.
   178→     * @return void
   179→     * @author Daniel Beltrán
   180→     */
   181→    public function generarAdecuacionHV(int $id): void {
   182→        $etapa_usuario = $this->repositorio->getUno($id);
   183→
   184→        // Validar que sea la etapa actual del aplicante y no que tenga puntaje
   185→        if ($etapa_usuario->etapa->nombre === EtapaVacante::REVISION_HV_NOMBRE && $etapa_usuario->activo && is_null($etapa_usuario->puntaje)) {
   186→            $perfil_laboral = $this->perfil_laboral_repositorio->getUnoAvanzado(new Request([
   187→                'usuario_id'                     => $etapa_usuario->usuario_id,
   188→                'incluir_experiencias_laborales' => true,
   189→                'incluir_estudios'               => true,
   190→                'incluir_idiomas'                => true,
   191→                'incluir_usuario'                => true
   192→            ]));
   193→
   194→            // Generar y guardar la adecuación del perfil de candidato (habilidades y herramientas tecnológicas)
   195→            $categoria            = 'perfil';
   196→            $puntaje_aspecto      = 100 / 2;
   197→            $datos_vacante        = $etapa_usuario->vacante->datos;
   198→            $habilidades_vacante  = $datos_vacante->where('llave', 'habilidades')->first();
   199→            $herramientas_vacante = $datos_vacante->where('llave', 'herramientas_tecnologicas')->first();
   200→
   201→            // Validar coincidencia para las habilidades
   202→            if (!is_null($habilidades_vacante->valor) && !empty($habilidades_vacante->valor)) {
   203→                $habilidades_vacante        = array_map(fn($item) => trim($item), explode(',', $habilidades_vacante->valor));
   204→                $habilidades_perfil_laboral = array_map(fn($item) => trim($item), explode(',', $perfil_laboral->habilidades));
   205→                $coincidencias              = count(array_uintersect($habilidades_vacante, $habilidades_perfil_laboral, 'strcasecmp'));
   206→
   207→                if ($coincidencias >= count($habilidades_vacante)) {
   208→                    $this->establecerPuntajeCumplimiento($puntaje_aspecto, true);
   209→                }
   210→            } else {
   211→                $this->establecerPuntajeCumplimiento($puntaje_aspecto, true);
   212→            }
   213→
   214→            $this->guardarAspectoPuntaje($etapa_usuario->id, $categoria, 'habilidades');
   215→
   216→            // Validar coincidencia para las herramientas tecnológicas
   217→            if (!is_null($herramientas_vacante->valor) && !empty($herramientas_vacante->valor)) {
   218→                $herramientas_vacante        = array_map(fn($item) => trim($item), explode(',', $herramientas_vacante->valor));
   219→                $herramientas_perfil_laboral = array_map(fn($item) => trim($item), explode(',', $perfil_laboral->herramientas_tecnologicas));
   220→                $coincidencias               = count(array_uintersect($herramientas_vacante, $herramientas_perfil_laboral, 'strcasecmp'));
   221→
   222→                if ($coincidencias >= count($herramientas_vacante)) {
   223→                    $this->establecerPuntajeCumplimiento($puntaje_aspecto, true);
   224→                }
   225→            } else {
   226→                $this->establecerPuntajeCumplimiento($puntaje_aspecto, true);
   227→            }
   228→
   229→            $this->guardarAspectoPuntaje($etapa_usuario->id, $categoria, 'herramientas');
   230→
   231→            // Generar y guardar la adecuación de requisitos del candidato (género, edad, nivel educativo, modalidad de trabajo, salario, experiencia, idiomas, licencia de conducir)
   232→            $categoria       = 'requisito';
   233→            $puntaje_aspecto = 100 / 8;
   234→
   235→            // Validar coincidencia de género
