   423→    /**
   424→     * Generar adecuación para preguntas prefiltro.
   425→     *
   426→     * Califica las respuestas del aplicante en cada una de las preguntas y calcula la adecuación en base a sus resultados.
   427→     *
   428→     * @param int $id ID de la etapa del usuario en la vacante.
   429→     * @return void
   430→     * @author Daniel Beltrán
   431→     */
   432→    public function generarAdecuacionPrefiltro(int $id): void {
   433→        $etapa_usuario = $this->repositorio->getUno($id);
   434→
   435→        // Validar que sea la etapa actual del aplicante y no que tenga puntaje
   436→        if ($etapa_usuario->etapa->nombre === EtapaVacante::PREGUNTAS_PREFILTRO_NOMBRE && $etapa_usuario->activo && is_null($etapa_usuario->puntaje)) {
   437→            $preguntas = $this->pregunta_oferta_laboral_repositorio->getTodo(new Request([
   438→                'etapa_vacante_id'   => $etapa_usuario->etapa_vacante_id,
   439→                'usuario_id'         => $etapa_usuario->usuario_id,
   440→                'incluir_respuestas' => true
   441→            ]));
   442→
   443→            $porcentaje_pregunta  = 100 / $preguntas->count();
   444→            $respuestas_correctas = 0;
   445→
   446→            foreach ($preguntas as $pregunta) {
   447→                $puntaje    = null;
   448→                $puntaje_ia = null;
   449→
   450→                if ($pregunta->es_abierta) {
   451→                    $respuesta = $opcion_seleccionada = $pregunta->respuestas->first();
   452→
   453→                    if (!is_null($respuesta)) {
   454→                        try {
   455→                            $peticion_ia = $this->realizarPeticionIA(
   456→                                ruta: 'calificar-pregunta-abierta',
   457→                                parametros: [
   458→                                    'pregunta'  => $pregunta->enunciado,
   459→                                    'respuesta' => $respuesta->respuesta,
   460→                                ],
   461→                                contexto_tracking: [
   462→                                    'vacante_id' => $etapa_usuario->etapa->vacante_id ?? null,
   463→                                    'agenda_id' => null,
   464→                                ]
   465→                            );
   466→
   467→                            if (!is_null($peticion_ia) && $peticion_ia['status_code'] === 200) {
   468→                                $puntaje_ia = $peticion_ia['calificacion'];
   469→                            }
   470→                        } catch (RequestException|ConnectionException $error) {}
   471→                    }
   472→                } else {
   473→                    $opciones_correctas = $pregunta->opciones->where('es_correcta', true);
   474→
   475→                    // Selección única
   476→                    if ($opciones_correctas->count() === 1) {
   477→                        $opcion_correcta     = $opciones_correctas->first();
   478→                        $opcion_seleccionada = $pregunta->respuestas->first();
   479→                        $puntaje             = (int) (!is_null($opcion_seleccionada) && $opcion_correcta->id === $opcion_seleccionada->opcion_pregunta_id);
   480→                    } else { // Selección múltiple
   481→                        $opciones_correctas     = $opciones_correctas->pluck('id')->toArray();
   482→                        $opciones_seleccionadas = $pregunta->respuestas->pluck('opcion_pregunta_id')->toArray();
   483→                        $aciertos               = count(array_intersect($opciones_seleccionadas, $opciones_correctas));
   484→                        $fallos                 = count(array_diff($opciones_seleccionadas, $opciones_correctas));
   485→                        $puntaje                = (int) ($aciertos - $fallos === count($opciones_correctas));
   486→                    }
   487→                }
   488→
   489→                // Guardar el puntaje obtenido en cada pregunta
   490→                $this->puntaje_pregunta_oferta_laboral_repositorio->crearSiNoExiste([
   491→                    'pregunta_id' => $pregunta->id,
   492→                    'usuario_id'  => $etapa_usuario->usuario_id
   493→                ], [
   494→                    'puntaje'     => $puntaje,
   495→                    'puntaje_ia'  => $puntaje_ia
   496→                ]);
   497→
   498→                // Sumar el puntaje total
   499→                if (is_null($puntaje) && !is_null($puntaje_ia)) {
   500→                    $respuestas_correctas += $puntaje_ia;
   501→                } else {
   502→                    $respuestas_correctas += $puntaje;
   503→                }
   504→            }
   505→
   506→            $puntaje_etapa = $respuestas_correctas * $porcentaje_pregunta;
   507→
   508→            if ($etapa_usuario->vacante->automatizar_etapas) {
   509→                // Obtener el movimiento de la etapa con sus configuraciones
   510→                $etapa_movimiento = $this->etapa_movimiento_vacante_repositorio->getUnoAvanzado(new Request([
   511→                    'etapa_vacante_id'        => $etapa_usuario->etapa_vacante_id,
   512→                    'movimiento_vacante_id'   => MovimientoVacante::PREGUNTAS_PREFILTRO_ID,
   513→                    'incluir_configuraciones' => true
   514→                ]));
   515→
   516→                $configuracion_preguntas_correctas = $etapa_movimiento->configuraciones->where('movimientoConfiguracion.llave', 'descartar_candidatos_respondieron_menos_preguntas_prefiltro')->first();
   517→
   518→                // Avanzar al aplicante de etapa o descartarlo según la configuración de la etapa
   519→                if (!is_null($configuracion_preguntas_correctas) && $configuracion_preguntas_correctas->valor <= $respuestas_correctas) {
   520→                    $this->guardarPuntajeYCambiarDeEtapa($etapa_usuario, $puntaje_etapa);
   521→                } else {
   522→                    $this->guardarPuntajeYCambiarDeEtapa($etapa_usuario, $puntaje_etapa, descartar: true);
   523→                }
   524→            } else {
   525→                $this->repositorio->actualizar($etapa_usuario->id, ['puntaje' => $puntaje_etapa]);
   526→            }
   527→        }
   528→    }
