   548→            }
   549→
   550→            if (in_array($etapa_usuario->etapa->nombre, [EtapaVacante::VIDEO_ENTREVISTA_NOMBRE, EtapaVacante::ENTREVISTA_NOMBRE])) {
   551→                if ($etapa_usuario->etapa->nombre === EtapaVacante::VIDEO_ENTREVISTA_NOMBRE) {
   552→                    $movimiento_vacante_id                   = MovimientoVacante::SOLICITUD_VIDEOENTREVISTA_ID;
