   555→                    $llave_configuracion_adecuaciones_medias = 'descartar_candidatos_adecuaciones_medias_videoentrevista';
   556→                } else {
   557→                    $movimiento_vacante_id                   = MovimientoVacante::SOLICITUD_ENTREVISTA_ID;
   558→                    $llave_configuracion_formato             = 'solicitar_formato_entrevista';
   559→                    $llave_configuracion_adecuaciones_bajas  = 'descartar_candidatos_adecuaciones_bajas_entrevista';
   560→                    $llave_configuracion_adecuaciones_medias = 'descartar_candidatos_adecuaciones_medias_entrevista';
   561→                }
   562→
   563→                if ($this->verificarFormatoEntrevista($etapa_usuario, $movimiento_vacante_id, $llave_configuracion_formato)) {
   564→                    $this->gestionarContinuidadAplicante($etapa_usuario, $movimiento_vacante_id, $puntaje, $llave_configuracion_adecuaciones_bajas, $llave_configuracion_adecuaciones_medias);
   565→                } else {
   566→                    $this->repositorio->actualizar($etapa_usuario->id, ['puntaje' => $puntaje]);
   567→                }
   568→
   569→                // TODO: Invocar esta función al momento de completar el formato de entrevista
   570→            }
   571→        }
   572→    }
