   628→
   629→        $configuracion_formato_entrevista = $etapa_movimiento->configuraciones->where('movimientoConfiguracion.llave', $llave_configuracion)->first();
   630→
   631→        if (!is_null($configuracion_formato_entrevista) && (bool) $configuracion_formato_entrevista->valor) {
   632→            if ($movimiento_vacante_id == MovimientoVacante::SOLICITUD_VIDEOENTREVISTA_ID) {
   633→                $formato_entrevista = $this->formato_entrevista_repositorio->getUnoAvanzado(new Request(['etapa_vacante_usuario_id' => $etapa_usuario->id]));
   634→
   635→                if (!is_null($formato_entrevista) && !is_null($formato_entrevista->descripcion_vacante) && !empty($formato_entrevista->descripcion_vacante)) {
   636→                    $formato_diligenciado = true;
