   655→     */
   656→    public function recalcularAdecuacionPrefiltro(int $etapa_vacante_id, int $usuario_id): void {
   657→        $etapa_usuario = $this->repositorio->getUnoAvanzado(new Request([
   658→            'etapa_vacante_id' => $etapa_vacante_id,
   659→            'usuario_id'       => $usuario_id
   660→        ]));
   661→
   662→        if ($etapa_usuario->activo) {
   663→            $preguntas = $this->pregunta_oferta_laboral_repositorio->getTodo(new Request([
   664→                'etapa_vacante_id' => $etapa_usuario->etapa_vacante_id,
   665→                'usuario_id'       => $etapa_usuario->usuario_id,
   666→                'incluir_puntajes' => true
   667→            ]));
   668→
   669→            $puntaje            = 0;
   670→            $puntaje_individual = 100 / $preguntas->count();
   671→
   672→            foreach ($preguntas as $pregunta) {
   673→                if (is_null($pregunta->puntajes[0]->puntaje) && !is_null($pregunta->puntajes[0]->puntaje_ia)) {
   674→                    $puntaje += $pregunta->puntajes[0]->puntaje_ia;
   675→                } elseif (!is_null($pregunta->puntajes[0]->puntaje)) {
   676→                    $puntaje += $pregunta->puntajes[0]->puntaje;
   677→                }
   678→            }
   679→
   680→            $this->repositorio->actualizar($etapa_usuario->id, ['puntaje' => $puntaje_individual * $puntaje]);
   681→        }
