   684→    /**
   685→     * Guardar el puntaje de la etapa actual y gestionar la continuidad del aplicante en la vacante.
   686→     *
   687→     * @param EtapaVacanteUsuario $etapa_usuario Etapa actual del usuario.
   688→     * @param int $movimiento_vacante_id ID del movimiento de la vacante.
   689→     * @param int $puntaje Puntaje obtenido en la etapa actual.
   690→     * @param string $llave_puntajes_bajos Llave de configuración para puntajes bajos.
   691→     * @param string $llave_puntajes_medios Llave de configuración para puntajes medios.
   692→     * @param bool $escala_pruebas_psicometricas Indica si debe utilizar la escala de puntajes de pruebas psicométricas.
   693→     * @return void
   694→     * @author Daniel Beltrán
   695→     */
   696→    public function gestionarContinuidadAplicante(EtapaVacanteUsuario $etapa_usuario, int $movimiento_vacante_id, int $puntaje, string $llave_puntajes_bajos, string $llave_puntajes_medios, bool $escala_pruebas_psicometricas = false) {
   697→        if ($etapa_usuario->vacante->automatizar_etapas) {
   698→            $calificacion_baja  = 40;
   699→            $calificacion_media = 70;
   700→
   701→            if ($escala_pruebas_psicometricas) {
   702→                $calificacion_baja  = 59;
   703→                $calificacion_media = 75;
   704→            }
   705→
   706→            // Obtener el movimiento de la etapa con sus configuraciones
   707→            $etapa_movimiento = $this->etapa_movimiento_vacante_repositorio->getUnoAvanzado(new Request([
   708→                'etapa_vacante_id'        => $etapa_usuario->etapa_vacante_id,
   709→                'movimiento_vacante_id'   => $movimiento_vacante_id,
   710→                'incluir_configuraciones' => true
   711→            ]));
   712→
   713→            $configuracion_adecuaciones_bajas  = $etapa_movimiento->configuraciones->where('movimientoConfiguracion.llave', $llave_puntajes_bajos)->first();
   714→            $configuracion_adecuaciones_medias = $etapa_movimiento->configuraciones->where('movimientoConfiguracion.llave', $llave_puntajes_medios)->first();
   715→
   716→            if (!is_null($configuracion_adecuaciones_medias) && filter_var($configuracion_adecuaciones_medias->valor, FILTER_VALIDATE_BOOLEAN) && $puntaje <= $calificacion_media) {
   717→                $this->guardarPuntajeYCambiarDeEtapa($etapa_usuario, $puntaje, descartar: true);
   718→                return;
   719→            } elseif (!is_null($configuracion_adecuaciones_bajas) && filter_var($configuracion_adecuaciones_bajas->valor, FILTER_VALIDATE_BOOLEAN) && $puntaje <= $calificacion_baja) {
   720→                $this->guardarPuntajeYCambiarDeEtapa($etapa_usuario, $puntaje, descartar: true);
   721→                return;
   722→            }
   723→
   724→            $this->guardarPuntajeYCambiarDeEtapa($etapa_usuario, $puntaje);
   725→        } else {
   726→            $this->repositorio->actualizar($etapa_usuario->id, ['puntaje' => $puntaje]);
   727→        }
   728→    }
