   730→    /**
   731→     * Guardar puntaje de la etapa actual y mover al aplicante a la siguiente etapa.
   732→     *
   733→     * @param EtapaVacanteUsuario $etapa_usuario Etapa actual del usuario.
   734→     * @param ?int $puntaje Puntaje obtenido en la etapa actual.
   735→     * @param ?int $puntaje_ia Puntaje obtenido por la IA en la etapa actual.
   736→     * @param ?string $notas_ia Notas dadas por la IA para la etapa actual.
   737→     * @param bool $descartar Indica si la etapa a la que se debe cambiar es 'descartados'.
   738→     * @return void
   739→     * @author Daniel Beltrán
   740→     * @author Andres Gonzalez
   741→     * @author Esperanza Olivo
   742→     */
   743→    public function guardarPuntajeYCambiarDeEtapa(EtapaVacanteUsuario $etapa_usuario, ?int $puntaje = null, ?int $puntaje_ia = null, ?string $notas_ia = null, bool $descartar = false): void {
   744→        if ($descartar) {
   745→            $etapa_siguiente = $this->etapa_vacante_repositorio->getUnoAvanzado(new Request(['vacante_id' => $etapa_usuario->vacante->id, 'nombre' => EtapaVacante::DESCARTADOS_NOMBRE]));
   746→        } else {
   747→            $etapa_siguiente = $this->etapa_vacante_repositorio->obtenerEtapaSiguiente($etapa_usuario->vacante->id, $etapa_usuario->etapa->orden);
   748→        }
   749→
   750→        $etapa_actual_activa = true;
   751→
   752→        if ($etapa_siguiente->nombre !== EtapaVacante::CONTRATADOS_NOMBRE) {
   753→            $etapa_actual_activa = false;
   754→            $this->guardarEtapa($etapa_usuario, $etapa_siguiente);
   755→        } elseif (!$etapa_usuario->vacante->automatizar_etapas) {
   756→            $etapa_actual_activa = false;
   757→            $this->guardarEtapa($etapa_usuario, $etapa_siguiente);
   758→        }
   759→
   760→        $this->repositorio->actualizar($etapa_usuario->id, [
   761→            'puntaje'    => $puntaje,
   762→            'puntaje_ia' => $puntaje_ia,
   763→            'notas_ia'   => $notas_ia,
   764→            'activo'     => $etapa_actual_activa
   765→        ]);
   766→
   767→        // Cuando la etapa es contratado, se valida si ya se han completado las vacantes requeridas para cerrar la vacante
   768→        if ($etapa_siguiente->nombre === EtapaVacante::CONTRATADOS_NOMBRE) {
   769→            if ($etapa_usuario->vacante->cantidad_vacantes === $this->repositorio->contarContratados($etapa_usuario->etapa->vacante_id)) {
   770→                $this->vacante_repositorio->actualizar($etapa_usuario->etapa->vacante_id, ['estado_id' => EstadoVacante::COMPLETADA_ID]);
   771→
   772→                $aplicantes_activos_restantes_ids = $this->repositorio->getTodo(new Request([
   773→                    'vacante_id'             => $etapa_usuario->etapa->vacante_id,
   774→                    'activo'                 => true,
   775→                    'excluir_etapas'         => [EtapaVacante::DESCARTADOS_NOMBRE, EtapaVacante::CONTRATADOS_NOMBRE]
   776→                ]))->pluck('id')->toArray();
   777→
   778→                // Descartar a los aplicantes que quedan activos en la vacante
   779→                $this->descartarAplicantesManual($aplicantes_activos_restantes_ids);
   780→
   781→                $aplicantes_activos_sin_notificar = $this->repositorio->getTodo(new Request([
   782→                    'vacante_id'             => $etapa_usuario->etapa->vacante_id,
   783→                    'activo'                 => true,
   784→                    'sin_fecha_notificacion' => true
   785→                ]));
   786→
   787→                foreach ($aplicantes_activos_sin_notificar as $aplicante) {
   788→                    $this->notificarCambioEtapa($aplicante, false);
   789→                }
   790→            }
   791→        }
   792→    }
