   827→    public function obtenerAdecuacionUsuario(int $usuario_id, int $vacante_id): ?int {
   828→        $etapas     = $this->repositorio->obtenerEtapasCompletadasUsuario($usuario_id, $vacante_id);
   829→        $adecuacion = 0;
   830→
   831→        foreach ($etapas as $etapa) {
