   842→    /**
   843→     * Verificar si una etapa ya ha sido completada por el aplicante.
   844→     *
   845→     * @param int $id ID de la etapa del usuario en la vacante (etapa_vacante_usuario_id).
   846→     * @return bool
   847→     * @author Daniel Beltrán
   848→     * @author Andres Gonzalez
   849→     */
   850→    public function verificarEtapaCompletada(int $id): bool {
   851→        $etapa_usuario    = $this->repositorio->getUno($id);
   852→        $etapa_completada = false;
   853→
   854→        // Verificar si la etapa de revisión de hoja de vida ya fue completada
   855→        if (in_array($etapa_usuario->etapa->nombre, [EtapaVacante::REVISION_HV_NOMBRE, EtapaVacante::PRUEBAS_PSICOMETRICAS_NOMBRE, EtapaVacante::VIDEO_ENTREVISTA_NOMBRE]) && !is_null($etapa_usuario->puntaje) || !is_null($etapa_usuario->puntaje_ia)) {
   856→            $etapa_completada = true;
   857→        }
   858→
   859→        if ($etapa_usuario->etapa->nombre === EtapaVacante::ENTREVISTA_NOMBRE) {
   860→            if ($this->configuracion_modelo_servicio->obtenerValorLlaveConfiguracion(app(VacanteService::class)->obtenerConfiguracionesEtapaAplicante($etapa_usuario->etapa_vacante_id), 'solicitar_formato_entrevista')) {
   861→                $formato_entrevista = $this->formato_entrevista_repositorio->getUnoAvanzado(new Request(['etapa_vacante_usuario_id' => $etapa_usuario->id]));
   862→
   863→                if (!is_null($formato_entrevista) && !is_null($formato_entrevista->descripcion_vacante) && !empty($formato_entrevista->descripcion_vacante)) {
   864→                    $etapa_completada = true;
   865→                }
   866→            } else {
   867→                $etapa_completada = true;
   868→            }
   869→        }
   870→
   871→        // Verificar si las etapas de preguntas prefiltro o videoentrevista ya han sido completadas
   872→        if (in_array($etapa_usuario->etapa->nombre, [EtapaVacante::PREGUNTAS_PREFILTRO_NOMBRE, EtapaVacante::VIDEO_ENTREVISTA_NOMBRE])) {
   873→            $preguntas = $this->pregunta_oferta_laboral_repositorio->getTodo(new Request([
   874→                'etapa_vacante_id'                      => $etapa_usuario->etapa_vacante_id,
   875→                'usuario_id'                            => $etapa_usuario->usuario_id,
   876→                'incluir_cantidad_respuestas_aplicante' => true
   877→            ]));
   878→
   879→            $preguntas_contestadas = 0;
   880→
   881→            foreach ($preguntas as $pregunta) {
   882→                if ($pregunta['respuestas_count'] > 0) {
   883→                    $preguntas_contestadas++;
   884→                }
   885→            }
   886→
   887→            // Validar que todas las preguntas hayan sido contestadas
   888→            if (count($preguntas) === $preguntas_contestadas) {
   889→                $etapa_completada = true;
   890→            }
   891→        }
   892→
   893→        if ($etapa_usuario->etapa->nombre === EtapaVacante::PRUEBA_TECNICA_NOMBRE) {
   894→            if ((!is_null($etapa_usuario->pruebaTecnica?->enlace) && !empty($etapa_usuario->pruebaTecnica?->enlace) || $etapa_usuario->pruebaTecnica?->archivos->count() > 0) && $etapa_usuario->pruebaTecnica?->borrador == false) {
   895→                $etapa_completada = true;
   896→            }
   897→        }
   898→
   899→        if ($etapa_usuario->etapa->nombre === EtapaVacante::DOCUMENTOS_CONTRATACION_NOMBRE) {
   900→            $documentos_contratacion = app(EtapaVacanteService::class)->obtenerDocumentosContratacion(
   901→                $etapa_usuario->etapa_vacante_id,
   902→                new Request(['etapa_vacante_usuario_id' => $etapa_usuario->id])
   903→            );
   904→
   905→            $documentos_solicitados     = collect($documentos_contratacion['documentos']);
   906→            $archivos_guardados         = collect($documentos_contratacion['archivos_guardados']);
   907→            $archivos_guardados_nombres = $archivos_guardados->pluck('nombre')->toArray();
   908→
   909→            $todos_cargados = true;
   910→            foreach ($documentos_solicitados as $documento) {
   911→                $nombre_normalizado = $this->normalizarNombreDocumento($documento['nombre']);
   912→
   913→                if (!in_array($nombre_normalizado, $archivos_guardados_nombres)) {
   914→                    $todos_cargados = false;
   915→                    break;
   916→                }
   917→            }
   918→
   919→            if ($todos_cargados && $documentos_solicitados->count() > 0 && !is_null($etapa_usuario->fecha_ejecucion)) {
   920→                $etapa_completada = true;
   921→            }
   922→        }
   923→
   924→        return $etapa_completada;
   925→    }
