    'sub'     => (string) $user->id,
    'email'   => $user->email,
    'name'    => $user->getNombreCompletoAttribute(),
    'nonce'   => $nonce,
    'exp'     => $exp,
    'student' => $studentData,
];

$token    = sso_sign_token($payload, config('services.sso.secret'));
$wp       = rtrim(config('services.sso.wp_url'), '/');
$cb       = ltrim(config('services.sso.wp_callback'), '/');
$redirect = $request->input('redirect', '/course-filter-sidebar-left/');

return redirect()->away(
    $wp . '/' . $cb . '?token=' . urlencode($token) . '&redirect=' . urlencode($redirect)
