function renderizarCompetenciasMejorar(data) {
    var html = '';

    if (!data.pruebas || data.pruebas.length === 0) {
        html = `<div class="alert alert-info">
            No se encontraron resultados de pruebas psicométricas
        </div>`;
    } else {
        data.pruebas.forEach(function(prueba) {
            html += `<div class="card mb-3">
