                    <h6>${prueba.nombre_prueba}</h6>
                </div>
                <div class="card-body">
                    <ul class="list-group">
            `;

            prueba.competencias.forEach(function(competencia, index) {
                var color_badge = competencia.puntaje < 40 ? 'bg-danger' :
                                 competencia.puntaje < 70 ? 'bg-warning' : 'bg-success';
                html += `
